    const [selectAnOperation,setSelectAnOperation] = useState('');
    const [currentOperations, setCurrentOperations] = useState([{operation:'',index:0}]);
    const allOperations = {
        payment: <PaymentFormComponent />
    };
    // const operationConstructor = (operation) => {}
    return (
        <div className="X X-fd-column row-gap-2-rem">
            <div className="X column-gap-1-rem">
                <div className="operation-number-box">
                    <h4></h4>
                </div>
                <div className="X X-fd-column operation-box">
                    <ChooseOperationComponent setOperation={{selectAnOperation,setSelectAnOperation}}/>
                    {allOperations.payment}
