import React, { useState } from "react";
import ChooseOperationComponent from "./ChooseOperationComponent";
import PaymentFormComponent from "./OperationForms/PaymentFormComponent";
import "../../../Styles/operation.css";
const OperationFormComponent = props => {
    const [currentOperations, setCurrentOperations] = useState([{operation:'', operationData: {}, id:1}]);
    const allOperations = {
        payment: <PaymentFormComponent/>
    };

    const setOperationForThisOne = (operation,id) => {
        let operationArrayCopy = [...currentOperations];
        operationArrayCopy.find((op,i)=>op.id == id).operation = operation;
        setCurrentOperations(operationArrayCopy);
    }
    const addAnOperation = () => {
        let operationArrayCopy = [...currentOperations];
        operationArrayCopy.push({operation:'',id:operationArrayCopy[operationArrayCopy.length - 1].id + 1});
        setCurrentOperations(operationArrayCopy);
    }
    const duplicateThisOperation = operation => {
        let operationArrayCopy = [...currentOperations];
        operationArrayCopy.push({operation:operation,id:operationArrayCopy[operationArrayCopy.length - 1].id + 1});
        setCurrentOperations(operationArrayCopy);
    }
    const deleteThisOperation = id => {
        let operationArrayCopy = [...currentOperations];
        
        setCurrentOperations(operationArrayCopy);
    }
    return (
        <div className="X X-fd-column row-gap-2-rem">

            {currentOperations.map((op,i)=>{
                return (
                    <div className="X column-gap-1-rem" key={i}>
                        <div className="X X-fd-column X-ai-center row-gap-4-px">
                            {/* list placement number */}
                            <div className="X X-jc-center X-ai-center operation-number-box">
                                <h4>{i + 1}</h4>
                            </div>
                            {/* DUPLICATE */}
                            <div className="duplicate-operation" onClick={e=>duplicateThisOperation(op.operation)}></div>
                            {/* DELETE */}
                            {i != 0 && <div className="delete-operation transition-ease" onClick={e=>deleteThisOperation(op.id)}></div>}
                        </div>
                        <div className="X X-fd-column width-100-percent">
                            {/* select operation type */}
                            <ChooseOperationComponent setOperation={{setOperationForThisOne}} currentOperation={op.operation} id={op.id}/>
                            {/* operation form */}
                            {allOperations[op.operation]}
                        </div>
                    </div>
                )
            })}
            <div className="width-100-percent X X-jc-center">
                {/* add a blank operation */}
                <div id="add-operation" className="transition-ease" onClick={addAnOperation}></div>
            </div>
            {JSON.stringify(currentOperations)}
        </div>

    )
}
export default OperationFormComponent;
